import React from 'react';
import 'font-awesome/css/font-awesome.min.css';

const LeftArrow = (props) => {
    return (
        <div className="back__arrow arrow" onClick={props.prevSlide}>
            <i className="fa fa-arrow-left"></i>
        </div>
    )
}

const RightArrow = (props) => {
    return (
        <div className="next__arrow arrow" onClick={props.nextSlide}>
            <i className="fa fa-arrow-right"></i>
        </div>
    )
}

const Slide = ({image}) => {
    const styles = {
        backgroundImage: `url(${image})`,
        backgroundSize: 'cover',
        backgroundRepeat: 'no-repeat',
        backgroundPosition: '50% 50%',
    }
    return <div className="slide" style={styles}></div>
}

class Slider extends React.Component {
    constructor(props){
        super(props)
         this.state = {
          images: [
            'https://placekitten.com/700/700',
            'https://placekitten.com/700/700',
            'https://placekitten.com/700/700',
            'https://placekitten.com/700/700',            
          ],
          currentSlide: 0,
          translateValue: 0,
        }
       }
       prevSlide = () => {
       }
       nextSlide = () => {
       }
       render() {
        return (
          <div className="slider">
              <div className="slider__container"
              style={{
                transform: `translateX(${this.state.translateValue}px)`,
                transition: 'transform ease-in-out 0.45s',
              }}>
              {
                this.state.images.map( (image, index) => ( <Slide key={index} image={image} />) )
              }
              </div>
             <Slide  image={'https://placekitten.com/700/700'} />
             <LeftArrow prevSlide={this.prevSlide} />
            <RightArrow nextSlide={this.nextSlide}/>
           </div>
        );
      }
}

export default Slider;
